--liquibase formatted sql
				
--changeset CHANDRAMANI:METADATA_STORE_TBL_1 runOnChange:true failOnError:true
--comment metadata type reference table
create TABLE IF NOT EXISTS METADATA_STORE_TBL (
	DATASOURCE VARCHAR(16777216) COMMENT 'BODS Extract Structure',
	SEGMENT VARCHAR(16777216) COMMENT 'For Hierarchy Datasets Sets Table Granularity',
	FIELDNAME VARCHAR(16777216) COMMENT 'Name Of Extract Column',
	POSN NUMBER(38,0) COMMENT 'Column Order Number',
	INTTYPE VARCHAR(16777216),
	TYPE VARCHAR(16777216) COMMENT 'SAP Data Type',
	LENGTH NUMBER(38,0),
	DECIMALS NUMBER(38,0) COMMENT 'Decimal Precision',
	OUTPUTLENG NUMBER(38,0) COMMENT 'Length Of The Column After Being Transformed By SAP Application Layer Logic',
	KEYFLAG VARCHAR(16777216),
	SEL_MANDATORY VARCHAR(16777216),
	SEL_EQUAL VARCHAR(16777216),
	SEL_BETWEEN VARCHAR(16777216),
	SEL_PATTERN VARCHAR(16777216),
	DELTAFIELD VARCHAR(16777216),
	DESCRIPTION VARCHAR(16777216) COMMENT 'Column Description',
	REFFIELD VARCHAR(16777216),
	CONVEXIT VARCHAR(16777216),
	LOWERCASE VARCHAR(16777216),
	SIGNFLAG VARCHAR(16777216),
	SELONLY VARCHAR(16777216),
	SEL_NONDEFAULT VARCHAR(16777216),
	TRANSFER VARCHAR(16777216),
	TRANSFER_NONDEFAULT VARCHAR(16777216),
	FIELD_TXTSH VARCHAR(16777216),
	FIELD_TXTMD VARCHAR(16777216),
	FIELD_TXTLG VARCHAR(16777216),
	DSRC_TYPE VARCHAR(16777216),
	DSRC_DELTA VARCHAR(16777216),
	DSRC_EXTRACTOR VARCHAR(16777216),
	DSRC_EXSTRUCT VARCHAR(16777216),
	DSRC_TXTSH VARCHAR(16777216),
	DSRC_TXTLG VARCHAR(16777216) COMMENT 'Table Description',
	DSRC_TSTPNM VARCHAR(16777216) COMMENT 'Structure Last Changed By',
	DSRC_TSTPDAT DATE COMMENT 'Structure Last Changed At',
	DSRC_TSTPTIM TIME(9) COMMENT 'Structure Last Changed On',
	INSERT_DATETIME TIMESTAMP_NTZ(9) DEFAULT CURRENT_TIMESTAMP() COMMENT 'Time Stamp Of Row Insert'
)COMMENT='Metadata to create SAP tables'
;